import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function RecipeList() {
  const [recipes, setRecipes] = useState([]);
  const [newRecipeTitle, setNewRecipeTitle] = useState('');
  const [newRecipeIngredients, setNewRecipeIngredients] = useState('');
  const [newRecipeInstructions, setNewRecipeInstructions] = useState('');
  const [isMounted, setIsMounted] = useState(true); // Add isMounted state

  useEffect(() => {
    setIsMounted(true); // Set isMounted to true when the component mounts
    axios.get('http://127.0.0.1:5556/recipes')
      .then(response => {
        if (isMounted) { // Check if the component is still mounted before updating state
          setRecipes(response.data);
        }
      })
      .catch(error => {
        console.error('Error fetching recipes:', error);
      });

    return () => {
      setIsMounted(false); // Set isMounted to false when the component unmounts
    };
  }, [isMounted]); // Add isMounted to dependencies array

  const handleCreateRecipe = (event) => {
    event.preventDefault(); // Prevent the default form submission behavior
    axios.post('http://127.0.0.1:5556/recipes', {
      title: newRecipeTitle,
      ingredients: newRecipeIngredients,
      instructions: newRecipeInstructions,
      // Add any other required fields here
    })
    .then(response => {
      setRecipes([...recipes, response.data]); // Update recipes state
      setNewRecipeTitle(''); // Reset form fields
      setNewRecipeIngredients('');
      setNewRecipeInstructions('');
    })
    .catch(error => {
      console.error('Error creating recipe:', error);
    });
  };

  return (
    <div>
      <h2>Recipe List</h2>
      <ul>
        {recipes.map(recipe => (
          <li key={recipe.id}>
            <Link to={`/recipes/${recipe.id}`}>{recipe.title}</Link>
          </li>
        ))}
      </ul>
      <h3>Create New Recipe</h3>
      <form onSubmit={handleCreateRecipe}>
        <label>Title:</label>
        <input type="text" value={newRecipeTitle} onChange={e => setNewRecipeTitle(e.target.value)} required />
        <label>Ingredients:</label>
        <textarea value={newRecipeIngredients} onChange={e => setNewRecipeIngredients(e.target.value)} required />
        <label>Instructions:</label>
        <textarea value={newRecipeInstructions} onChange={e => setNewRecipeInstructions(e.target.value)} required />
        <button type="submit">Create Recipe</button>
      </form>
    </div>
  );
}

export default RecipeList;
