import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import RecipeList from './components/RecipeList';
import RecipeDetail from './components/RecipeDetail';
import RecipeForm from './components/RecipeForm';
import CategoryList from './components/CategoryList';
import CategoryForm from './components/CategoryForm';
import Home from './components/Home';
import Ratings from './components/Ratings';
import Users from './components/Users';

function App() {
  const [recipes, setRecipes] = useState([]);
  const [categories, setCategories] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchRecipes();
    fetchCategories();
    fetchRatings();
    fetchUsers();
  }, []);

  const fetchRecipes = () => {
    fetch('http://127.0.0.1:5556/recipes')
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch recipes');
        }
        return response.json();
      })
      .then(data => setRecipes(data))
      .catch(error => console.error('Error fetching recipes:', error));
  };

  const fetchCategories = () => {
    fetch('http://127.0.0.1:5556/categories')
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch categories');
        }
        return response.json();
      })
      .then(data => setCategories(data))
      .catch(error => console.error('Error fetching categories:', error));
  };

  const fetchRatings = () => {
    fetch('http://127.0.0.1:5556/ratings')
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch ratings');
        }
        return response.json();
      })
      .then(data => setRatings(data))
      .catch(error => console.error('Error fetching ratings:', error));
  };

  const fetchUsers = () => {
    fetch('http://127.0.0.1:5556/users')
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch users');
        }
        return response.json();
      })
      .then(data => setUsers(data))
      .catch(error => console.error('Error fetching users:', error));
  };

  return (
    <Router>
      <div>
        <Header />
        <Route path="/recipes" exact component={() => <RecipeList recipes={recipes} />} />
        <Route path="/recipes/:id" component={() => <RecipeDetail recipes={recipes} />} />
        <Route path="/recipes/:id" component={RecipeDetail} />
        <Route path="/recipes/new" component={() => <RecipeForm categories={categories} />} />
        <Route path="/categories" exact component={() => <CategoryList categories={categories} />} />
        <Route path="/categories/:id" component={CategoryForm} />
        <Route path="/ratings" exact component={() => <Ratings ratings={ratings} />} />
        <Route path="/ratings/:id" component={Ratings} />
        <Route path="/users" exact component={() => <Users users={users} />} />
        <Route path="/users/:id" component={Users} />
        <Route path="/home" exact component={Home} />
        <Footer />
      </div>
    </Router>
  );
}

export default App;
