import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useHistory } from 'react-router-dom';

function RecipeDetail() {
  const [recipe, setRecipe] = useState(null);
  const [newRecipeTitle, setNewRecipeTitle] = useState('');
  const [newRecipeIngredients, setNewRecipeIngredients] = useState('');
  const [newRecipeInstructions, setNewRecipeInstructions] = useState('');
  const [isMounted, setIsMounted] = useState(true); // Add a mounted state flag
  const { id } = useParams();
  const history = useHistory();

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:5556/recipes/${id}`);
        if (isMounted) { // Check if component is still mounted before updating state
          setRecipe(response.data);
        }
      } catch (error) {
        console.error('Error fetching recipe:', error);
        // Handle error, such as setting an error state or displaying an error message
      }
    };

    fetchRecipe();

    // Cleanup function to set isMounted to false when component unmounts
    return () => {
      setIsMounted(false);
    };
  }, [id, isMounted]); // Add isMounted to dependency array

  const handleCreateRecipe = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:5556/recipes', {
        title: newRecipeTitle,
        ingredients: newRecipeIngredients,
        instructions: newRecipeInstructions,
        // Add any other required fields here
      });
      if (isMounted) { // Check if component is still mounted before updating state
        setRecipe(response.data);
        setNewRecipeTitle('');
        setNewRecipeIngredients('');
        setNewRecipeInstructions('');
      }
    } catch (error) {
      console.error('Error creating recipe:', error);
      // Handle error, such as setting an error state or displaying an error message
    }
  };

  const handleDeleteRecipe = async () => {
    try {
      await axios.delete(`http://127.0.0.1:5556/recipes/${id}`);
      // Redirect to recipe list page after deletion
      history.push('/recipes');
    } catch (error) {
      console.error('Error deleting recipe:', error);
      // Handle error, such as setting an error state or displaying an error message
    }
  };

  if (!recipe) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{recipe.title}</h2>
      <p>Ingredients: {recipe.ingredients}</p>
      <p>Instructions: {recipe.instructions}</p>
      {/* Add rating display and interaction here */}
      <div>
        <h3>Create New Recipe</h3>
        <form onSubmit={handleCreateRecipe}>
          <label>Title:</label>
          <input type="text" value={newRecipeTitle} onChange={e => setNewRecipeTitle(e.target.value)} required />
          <label>Ingredients:</label>
          <textarea value={newRecipeIngredients} onChange={e => setNewRecipeIngredients(e.target.value)} required />
          <label>Instructions:</label>
          <textarea value={newRecipeInstructions} onChange={e => setNewRecipeInstructions(e.target.value)} required />
          <button type="submit">Create Recipe</button>
        </form>
        <button onClick={handleDeleteRecipe}>Delete Recipe</button>
      </div>
    </div>
  );
}

export default RecipeDetail;
