import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function CategoryList() {
  const [categories, setCategories] = useState([]);
  const { id } = useParams(); // Get category ID from URL params

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = () => {
    axios.get('http://127.0.0.1:5556/categories')
      .then(response => {
        setCategories(response.data);
      })
      .catch(error => {
        console.error('Error fetching categories:', error);
      });
  };

  const handleUpdateCategory = (categoryId) => {
    // You can implement the logic for updating categories here
    console.log('Update category:', categoryId);
  };

  return (
    <div className="category-list-container">
      <h2>Category List</h2>
      <ul>
        {categories.map(category => (
          <li key={category.id}>
            <h3>{category.name}</h3>
            {/* Button to update existing category */}
            <button onClick={() => handleUpdateCategory(category.id)}>Update Category</button>
            {/* Display recipes associated with the category */}
            <ul>
              {category.recipes.map(recipe => (
                <li key={recipe.id}>{recipe.title}</li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CategoryList;
