import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function RecipeList() {
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [ratedRecipe, setRatedRecipe] = useState(null); // State to store the recipe rated by the user
  const [ratingValue, setRatingValue] = useState(null); // State to store the selected rating value

  useEffect(() => {
    axios.get('http://127.0.0.1:5556/recipes')
      .then(response => {
        setRecipes(response.data);
        setIsLoading(false);
      })
      .catch(error => {
        setError(error);
        setIsLoading(false);
      });
  }, []);

  const handleDeleteRecipe = (id) => {
    axios.delete(`http://127.0.0.1:5556/recipes/${id}`)
      .then(response => {
        setRecipes(recipes.filter(recipe => recipe.id !== id));
      })
      .catch(error => {
        console.error('Error deleting recipe:', error);
      });
  };

  const handleRateRecipe = (id, rating) => {
    // Send rating to the backend and update the recipe's rating
    // For example:
    // axios.post(`http://127.0.0.1:5556/recipes/${id}/rate`, { rating })
    //   .then(response => {
    //     // Update the recipe list with the new rating
    //     setRatedRecipe(id);
    //     setRatingValue(rating);
    //   })
    //   .catch(error => {
    //     console.error('Error rating recipe:', error);
    //   });

    // For demonstration purpose, setting the state directly
    setRatedRecipe(id);
    setRatingValue(rating);
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div>
      <h2>Recipe List</h2>
      {recipes.map(recipe => (
        <div key={recipe.id}>
          <h3>{recipe.title}</h3>
          <p>Ingredients: {recipe.ingredients}</p>
          <p>Instructions: {recipe.instructions}</p>
          <p>User ID: {recipe.user_id}</p>
          <p>Category ID: {recipe.category_id}</p>
          <button onClick={() => handleDeleteRecipe(recipe.id)}>Delete</button>
          <div>
            Rate:
            {[1, 2, 3, 4, 5].map(rating => (
              <button key={rating} onClick={() => handleRateRecipe(recipe.id, rating)}>{rating}</button>
            ))}
          </div>
          {ratedRecipe === recipe.id && (
            <p>You rated "{recipe.title}" with {ratingValue}</p>
          )}
        </div>
      ))}
      <Link to="/recipes/new">Create New Recipe</Link>
    </div>
  );
}

export default RecipeList;
