import React, { useState, useEffect } from 'react';

function RecipeForm({ recipeToEdit }) {
  const [title, setTitle] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [instructions, setInstructions] = useState('');

  useEffect(() => {
    if (recipeToEdit) {
      setTitle(recipeToEdit.title);
      setIngredients(recipeToEdit.ingredients);
      setInstructions(recipeToEdit.instructions);
    }
  }, [recipeToEdit]);

  const handleSubmit = (event) => {
    event.preventDefault();
    // Submit the form data to create or edit the recipe
    const formData = {
      title,
      ingredients,
      instructions
    };
    if (recipeToEdit) {
      // Handle edit
      // Call editRecipe function with formData and recipeToEdit.id
    } else {
      // Handle create
      // Call createRecipe function with formData
    }
  };

  return (
    <div>
      <h2>{recipeToEdit ? 'Edit Recipe' : 'Create Recipe'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title:</label>
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
        </div>
        <div>
          <label>Ingredients:</label>
          <textarea value={ingredients} onChange={(e) => setIngredients(e.target.value)} required />
        </div>
        <div>
          <label>Instructions:</label>
          <textarea value={instructions} onChange={(e) => setInstructions(e.target.value)} required />
        </div>
        <button type="submit">{recipeToEdit ? 'Update Recipe' : 'Create Recipe'}</button>
      </form>
    </div>
  );
}

export default RecipeForm;
