import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function RecipeDetail() {
  const [recipe, setRecipe] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:5556/recipes/${id}`); // Update the URL to match your backend API endpoint
        setRecipe(response.data);
      } catch (error) {
        console.error('Error fetching recipe:', error);
        // Handle error, such as setting an error state or displaying an error message
      }
    };

    fetchRecipe();
  }, [id]);

  if (!recipe) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{recipe.title}</h2>
      <p>Ingredients: {recipe.ingredients}</p>
      <p>Instructions: {recipe.instructions}</p>
      {/* Add rating display here if needed */}
    </div>
  );
}

export default RecipeDetail;
