// Import statements...

function RecipeList() {
  // State declarations...

  // useEffect hook...

  // handleDeleteRecipe function...

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div>
      <h2>Recipe List</h2>
      {recipes.map(recipe => (
        <div key={recipe.id}>
          <h3>{recipe.title}</h3>
          <p>Ingredients: {recipe.ingredients}</p>
          <p>Instructions: {recipe.instructions}</p>
          {recipe.image_url && <img src={recipe.image_url} alt={recipe.title} />} {/* Display image if image_url exists */}
          <button onClick={() => handleDeleteRecipe(recipe.id)}>Delete</button>
        </div>
      ))}
      <Link to="/recipes/new">Create New Recipe</Link>
    </div>
  );
}

export default RecipeList;
