import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function CategoryList() {
  const [categories, setCategories] = useState([]);
  const [updatedCategoryName, setUpdatedCategoryName] = useState('');
  const { id } = useParams(); // Get category ID from URL params

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = () => {
    axios.get('http://127.0.0.1:5556/categories')
      .then(response => {
        setCategories(response.data);
      })
      .catch(error => {
        console.error('Error fetching categories:', error);
      });
  };

  const handleUpdateCategory = (categoryId) => {
    const updatedCategories = categories.map(category => {
      if (category.id === categoryId) {
        return { ...category, name: updatedCategoryName };
      }
      return category;
    });

    axios.put(`http://127.0.0.1:5556/categories/${categoryId}`, { name: updatedCategoryName })
      .then(() => {
        setCategories(updatedCategories);
        setUpdatedCategoryName('');
      })
      .catch(error => {
        console.error('Error updating category:', error);
      });
  };

  return (
    <div className="category-list-container">
      <h2>Category List</h2>
      <ul>
        {categories.map(category => (
          <li key={category.id}>
            <input
              type="text"
              value={updatedCategoryName}
              onChange={e => setUpdatedCategoryName(e.target.value)}
              placeholder="Enter updated category name"
            />
            <button onClick={() => handleUpdateCategory(category.id)}>Update Category</button>
            <h3>{category.name}</h3>
            {/* Display recipes associated with the category */}
            <ul>
              {category.recipes.map(recipe => (
                <li key={recipe.id}>{recipe.title}</li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CategoryList;
