import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function CategoryList() {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [newCategoryName, setNewCategoryName] = useState('');
  const [newRecipeName, setNewRecipeName] = useState('');
  const [newRecipeInstructions, setNewRecipeInstructions] = useState('');

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = () => {
    axios.get('http://127.0.0.1:5556/categories')
      .then(response => {
        setCategories(response.data);
      })
      .catch(error => {
        console.error('Error fetching categories:', error);
      });
  };

  const handleCategoryClick = (categoryId) => {
    axios.get(`http://127.0.0.1:5556/categories/${categoryId}/recipes`)
      .then(response => {
        setSelectedCategory(response.data);
      })
      .catch(error => {
        console.error('Error fetching recipes for category:', error);
      });
  };

  const handleNewCategoryChange = (event) => {
    setNewCategoryName(event.target.value);
  };

  const handleNewRecipeChange = (event) => {
    setNewRecipeName(event.target.value);
  };

  const handleNewRecipeInstructionsChange = (event) => {
    setNewRecipeInstructions(event.target.value);
  };

  const handleNewCategorySubmit = (event) => {
    event.preventDefault();
    axios.post('http://127.0.0.1:5556/categories', { name: newCategoryName })
      .then(response => {
        const newCategory = response.data;
        // Create the recipe associated with the new category
        axios.post('http://127.0.0.1:5556/recipes', {
          title: newRecipeName,
          instructions: newRecipeInstructions,
          category_id: newCategory.id
        })
        .then(() => {
          // After successfully creating the category and recipe, fetch categories again
          fetchCategories();
          // Reset form fields
          setNewCategoryName('');
          setNewRecipeName('');
          setNewRecipeInstructions('');
        })
        .catch(error => {
          console.error('Error creating new recipe:', error);
        });
      })
      .catch(error => {
        console.error('Error creating new category:', error);
      });
  };

  const handleDeleteCategory = (categoryId) => {
    axios.delete(`http://127.0.0.1:5556/categories/${categoryId}`)
      .then(() => {
        // After successfully deleting the category, fetch categories again
        fetchCategories();
      })
      .catch(error => {
        console.error('Error deleting category:', error);
      });
  };

  return (
    <div>
      <h2>Category List</h2>
      <ul>
        {categories.map(category => (
          <li key={category.id}>
            <button onClick={() => handleCategoryClick(category.id)}>{category.name}</button>
            <button onClick={() => handleDeleteCategory(category.id)}>Delete</button>
          </li>
        ))}
      </ul>
      <form onSubmit={handleNewCategorySubmit}>
        <input
          type="text"
          value={newCategoryName}
          onChange={handleNewCategoryChange}
          placeholder="Enter new category name"
        />
        <input
          type="text"
          value={newRecipeName}
          onChange={handleNewRecipeChange}
          placeholder="Enter new recipe name"
        />
        <textarea
          value={newRecipeInstructions}
          onChange={handleNewRecipeInstructionsChange}
          placeholder="Enter recipe instructions"
        />
        <button type="submit">Add Category</button>
      </form>
      {selectedCategory && (
        <div>
          <h2>Recipes for {selectedCategory.name}</h2>
          <ul>
            {selectedCategory.recipes.map(recipe => (
              <li key={recipe.id}>
                <div>
                  <strong>{recipe.title}</strong>
                  <p>{recipe.instructions}</p>
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default CategoryList;
