import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function RecipeForm() {
  const [title, setTitle] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [instructions, setInstructions] = useState('');
  const { id } = useParams(); // Get recipe ID from URL params

  useEffect(() => {
    // Fetch existing recipe data if editing
    if (id) {
      axios.get(`http://127.0.0.1:5556/recipes/${id}`)
        .then(response => {
          const { title, ingredients, instructions } = response.data;
          setTitle(title);
          setIngredients(ingredients);
          setInstructions(instructions);
        })
        .catch(error => {
          console.error('Error fetching recipe for editing:', error);
        });
    }
  }, [id]);

  const handleSubmit = (event) => {
    event.preventDefault();

    const recipeData = {
      title,
      ingredients,
      instructions
    };

    if (id) {
      // Edit existing recipe
      axios.patch(`http://127.0.0.1:5556/recipes/${id}`, recipeData)
        .then(response => {
          console.log('Recipe updated:', response.data);
          // Redirect or show success message
        })
        .catch(error => {
          console.error('Error updating recipe:', error);
        });
    } else {
      // Create new recipe
      axios.post('http://127.0.0.1:5556/recipes', recipeData)
        .then(response => {
          console.log('Recipe created:', response.data);
          // Redirect or show success message
        })
        .catch(error => {
          console.error('Error creating recipe:', error);
        });
    }
  };

  return (
    <div>
      <h2>{id ? 'Edit Recipe' : 'Create Recipe'}</h2>
      <form onSubmit={handleSubmit}>
        <label>Title:</label>
        <input type="text" value={title} onChange={e => setTitle(e.target.value)} required />
        <label>Ingredients:</label>
        <textarea value={ingredients} onChange={e => setIngredients(e.target.value)} required />
        <label>Instructions:</label>
        <textarea value={instructions} onChange={e => setInstructions(e.target.value)} required />
        <button type="submit">{id ? 'Update Recipe' : 'Create Recipe'}</button>
      </form>
    </div>
  );
}

export default RecipeForm;
