import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function CategoryForm() {
  const [currentCategoryName, setCurrentCategoryName] = useState('');
  const [updatedCategoryName, setUpdatedCategoryName] = useState('');
  const { id } = useParams(); // Get category ID from URL params

  useEffect(() => {
    fetchCategoryName();
  }, []);

  const fetchCategoryName = () => {
    axios.get(`http://127.0.0.1:5556/categories/${id}`)
      .then(response => {
        setCurrentCategoryName(response.data.name); // Set the current category name
        setUpdatedCategoryName(response.data.name); // Set the updated category name
      })
      .catch(error => {
        console.error('Error fetching category name:', error);
      });
  };

  const handleUpdateCategory = () => {
    axios.put(`http://127.0.0.1:5556/categories/${id}`, { name: updatedCategoryName })
      .then(() => {
        console.log('Category updated successfully');
        setCurrentCategoryName(updatedCategoryName); // Update the current category name
      })
      .catch(error => {
        console.error('Error updating category:', error);
      });
  };

  return (
    <div>
      <h2>Edit Category</h2>
      <div>
        <label htmlFor="currentCategoryName">Current Category Name:</label>
        <input
          type="text"
          id="currentCategoryName"
          value={currentCategoryName} // Use currentCategoryName as value
          disabled // Disabled for display only
        />
      </div>
      <div>
        <label htmlFor="newCategoryName">New Updated Category Name:</label>
        <input
          type="text"
          id="newCategoryName"
          value={updatedCategoryName}
          onChange={e => setUpdatedCategoryName(e.target.value)}
          placeholder="Enter updated category name"
        />
      </div>
      <button onClick={handleUpdateCategory}>Update Category</button>
    </div>
  );
}

export default CategoryForm;
